import React, { Component } from 'react';
import { Link } from 'react-router-dom';

export default class Navbar extends Component {

  handleClick = () => {
    console.log('Button clicked! This is: ', this)

    const items = document.querySelectorAll('.nav-item');
    const indicator = document.querySelector('.nav-indicator');

    items.forEach(item => {
      item.classList.remove('is-active');
      item.removeAttribute('style');
    });



  }

  render() {
    return (
      <nav className="nav">
        <Link to="/" className="nav-item is-active" active-color="red" onClick={this.handleClick}>About</Link>
        <Link to="/contact" className="nav-item" active-color="green" onClick={this.handleClick}>Contact</Link>
        <span className="nav-indicator"></span>
      </nav>

    );
  }
}

