import React, { Component } from 'react';
import styles from '../styles/single.module.css';
const jsdom = require("jsdom");
const { JSDOM } = jsdom;

export default class Single extends Component {
    constructor(props) {
        super(props);
     }

    getPost() {
        if (this.props.location.state) {
            let doc = new JSDOM(this.props.location.state.post);
            doc = doc.window.document.querySelector('body');
            console.log(doc.documentElement.outerHTML);

            let hello = doc.window.document.documentElement.outerHTML;
            return (
                <div className={styles.box}>
                <p>What I wish I knew before I tried to install Kali Linux.</p>
                <p>&nbsp;</p>
                <ol start='' >
                <li><p>Dual Boot</p>
                <ul>
                <li>Faster (by how much?)</li>

                </ul>
                </li>
                <li><p>USB or &quot;Live&quot; Boot</p>
                <ul>
                <li>Portable (but not MacOs vs. Windows)</li>
                <li>Doesn&#39;t take up storage on your hard drive. </li>
                <li>Can choose to &quot;persist data&quot; or not persist data each session. If you&#39;re running from the cops, this is probably a good idea.</li>
                <li>If you don&#39;t have a USB 3 port, don&#39;t bother</li>
                <li></li>

                </ul>
                </li>
                <li><p>Virtual Machine</p>
                <ul>
                <li>Can use both at once</li>

                </ul>
                </li>
                <li><p>Raspberry Pi</p>
                <ul>
                <li>Can use both at once</li>

                </ul>
                </li>

                </ol>
                <p>&nbsp;</p>
                </div>
            )
        } 
    }

    render() {
        return (
            <>{this.getPost()}</>
        );
    }
}

