import React, { Component } from 'react';
import styles from '../styles/tagbar.module.css';

export default class About extends Component {


    listByTag = (e) => {
        const data = this.props.data;

        this.props.data.map(currentProject => {
            currentProject.tags.map(currentTag => {
                if (currentTag == e.target.innerHTML.toLowerCase()) {
                    /* Project has tag */
                    console.log(currentProject);


                }


            })
        })






    }

    render() {

        return (
            <div className={styles.tagbar}>
                <button onClick={this.listByTag} className={styles.tag}>Python</button>
            </div>
        )

    }
  }

